import React, { Component } from 'react';
import "./App.css";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isSubmit: false,
      data: [],
      newName: "匿名",
      newText: "ここに入力",
    }
    this.getData();
    this.handleSubmit();
    this.addComment();
    this.handleChangeName = this.handleChangeName.bind(this);
    this.handleChangeText = this.handleChangeText.bind(this);
  }

  getData() {
    fetch('/data')
      .then(response => response.json())
      .then(temp => {
        this.setState({
          data: temp.commentInfo
        });
      });
  }

  handleChangeName(event) {
    this.setState({newName: event.target.newName});
    console.log(this.state.newName);
  }

  handleChangeText(event) {
    this.setState({newText: event.target.newText});
    console.log(this.state.newText);
  }

  handleSubmit() {
    if(this.state.isSubmit) {
      this.setState({isSubmit: false});
    } else {
      this.addComment();
      this.setState({isSubmit: true});
    }
    //console.log(this.state.isSubmit);
    return false;
  }

  addComment() {
    const numLast = this.state.data.slice(-1)[0];
    const numLastID = numLast;
    const newData = [...this.state.data, {id: numLastID, name: this.state.newName, text: this.state.newText}];
    console.log(newData);
  }



  render() {

    let newMessage;
    if (this.state.isSubmit) {
      newMessage = (
        <div className="newMes">
          <p>送信完了!!</p>
          <form onSubmit={()=>this.handleSubmit()}>
            <input type="submit" value="OK" />
          </form>
        </div>
      );
    } else {
      newMessage = (
        <div className="newMes">
          <form onSubmit={()=>this.handleSubmit()}>
            <p style={{fontSize: "20px", textDecoration: "underline"}}>新規コメント</p>
            <p>名前</p>
            <input type="text" onChange={this.handleChangeName}/>
            <p>コメント内容</p>
            <div className="newT">
              <textarea onChange={this.handleChangeText} />
              <input type="submit" value="送信" />
            </div>

          </form>
        </div>
      );
    }

    return (
      <div className="App">
        <h1>自由に話そう</h1>
        <hr style={{backgroundColor: "black"}}></hr>
        <ul className="showCom">

        {this.state.data.map((temp) => 
          <li key={temp.id}>
            <div className="showName">
              <p>{temp.name}</p>
            </div>
            <div className="showText">
              <p>{temp.text}</p>
            </div>
            <hr></hr>
          </li>
        )}
        <hr className="bottomLine"></hr>
        </ul>

      <div>
        {newMessage}
      </div>

      </div>
    );
  }
}
export default App;
