import React, { Component } from 'react';
import "./App.css";

let dataLastID=0;

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isSubmit: false,
      data: [],
      originData: [],
      newName: "匿名",
      newText: " ",
    }
    this.initialGetData();
    this.handleChangeName = this.handleChangeName.bind(this);
    this.handleChangeText = this.handleChangeText.bind(this);
  }

  initialGetData() {
    fetch('/data')
      .then(response => response.json())
      .then(temp => {
        this.setState({
          data: temp,
          originData: temp
        });
        dataLastID=this.state.data.length;
      });
  }

  getData() {
    fetch('/data')
      .then(response => response.json())
      .then(temp => {
        this.setState({
          data: temp
        });
        dataLastID=this.state.data.length;
      });
  }

  addData() {
    const sendData = {name: this.state.newName, text: this.state.newText};
    const param  = {
      method: "POST",
      headers: {
        "Content-Type": "application/json; charset=utf-8"
      },

      // リクエストボディ
      body: JSON.stringify(sendData)
    };

    fetch('/data/create', param)
      .then((res) => res.text())
      .then((data) => console.log( data ))
      .catch((err) => console.error( err ));
  }

  deleteData(tempID, event) {
    event.preventDefault();
    const sendData = {id: tempID};
    const param  = {
      method: "POST",
      headers: {
        "Content-Type": "application/json; charset=utf-8"
      },

      // リクエストボディ
      body: JSON.stringify(sendData)
    };

    fetch('/data/delete', param)
      .then((res) => res.text())
      .then((data) => console.log( data ))
      .catch((err) => console.error( err ));

    this.getData();
  }

  handleChangeName(event) {
    this.setState({newName: event.target.value});
  }

  handleChangeText(event) {
    this.setState({newText: event.target.value});
  }

  handleSubmit() {
    if(this.state.isSubmit) {
      this.setState({isSubmit: false});
    } else {
      this.addComment();
      this.setState({isSubmit: true});
    }
    //console.log(this.state.isSubmit);
    return false;
  }

  addComment() {
    this.addData();
    this.getData();
  }

  render() {

    let newMessage;
    if (this.state.isSubmit) {
      newMessage = (
        <div className="newMes">
          <p>送信完了!!</p>
          <form onSubmit={()=>this.handleSubmit()}>
            <input type="submit" value="OK" />
          </form>
        </div>
      );
    } else {
      newMessage = (
        <div className="newMes">
          <form onSubmit={()=>this.handleSubmit()}>
            <p style={{fontSize: "20px", textDecoration: "underline"}}>新規コメント</p>
            <p>名前</p>
            <input type="text" onChange={this.handleChangeName}/>
            <p>コメント内容</p>
            <div className="newT">
              <textarea onChange={this.handleChangeText} />
              <input type="submit" value="送信" />
            </div>

          </form>
        </div>
      );
    }

    return (
      <div className="App">
        <h1>自由に話そう</h1>
        <hr style={{backgroundColor: "black"}}></hr>
        <ul className="showCom">

        {this.state.data.map((temp) => 
          <li key={temp.id} style={{listStyle: "none"}}>
            <div className="showName">
              <p>{temp.name}</p>
            </div>
            <div className="textFlex">
              <p>{temp.text}</p>
              <a href="#" onClick={()=>this.deleteData(temp.id, event)}>削除</a>
            </div>

            <hr></hr>
          </li>
        )}
        <hr className="bottomLine"></hr>
        </ul>

      <div>
        {newMessage}
      </div>

      </div>
    );
  }
}
export default App;
